import React, { useState, useEffect } from 'react';
import { fetchProducts, fetchCategories } from './api/productsApi';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Card, Form, Navbar, Nav, Dropdown, DropdownButton } from 'react-bootstrap';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import CartPage from './cartPage';
import { useCart } from './cartContext';

function App() {
  const [products, setProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]); // Store all products for search filtering
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchProducts().then((fetchedProducts) => {
      setProducts(fetchedProducts);
      setAllProducts(fetchedProducts); // Initialize allProducts with fetched products
    });
    fetchCategories().then(setCategories);
  }, []);

  useEffect(() => {
    if (selectedCategory) {
      const formattedCategory = encodeURIComponent(selectedCategory);
      fetch(`https://dummyjson.com/products/category/${formattedCategory}`)
        .then((response) => response.json())
        .then((data) => {
          setProducts(data.products);
        })
        .catch((error) => {
          console.error('Failed to fetch products:', error);
          // Handle the error (e.g., set an error state, show a message, etc.)
        });
    } else {
      // Fetch all products or handle the 'no category selected' state
      fetchProducts().then(setProducts);
    }
  }, [selectedCategory]);

  const { addToCart } = useCart();

  return (
    <Router>
      <Navbar bg="light" expand="lg">
        <Navbar.Brand as={Link} to="/">My E-Commerce App</Navbar.Brand>
        <Nav className="mr-auto">
          <Nav.Link as={Link} to="/cart">Cart</Nav.Link>
        </Nav>
        <Form inline>
          <Form.Control
            type="text"
            placeholder="Search by name"
            className="mr-sm-2"
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </Form>
        <DropdownButton id="dropdown-basic-button" title={selectedCategory || "Select Category"}>
          <Dropdown.Item onClick={() => setSelectedCategory('')}>All Categories</Dropdown.Item>
          {categories.map((category, index) => (
            <Dropdown.Item key={index} onClick={() => setSelectedCategory(category)}>
              {category}
            </Dropdown.Item>
          ))}
        </DropdownButton>
      </Navbar>

      <Routes>
        <Route path="/cart" element={<CartPage />} />
        <Route path="/" element={
          <Container>
            <Row>
              {products.map(product => (
                <Col key={product.id} sm={12} md={6} lg={4} xl={3}>
                  <Card>
                    <Card.Img variant="top" src={product.thumbnail} />
                    <Card.Body>
                      <Card.Title>{product.title}</Card.Title>
                      <Card.Text>${product.price}</Card.Text>
                      <button onClick={() => addToCart(product)}>Add to Cart</button>
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </Row>
          </Container>
        } />
      </Routes>
    </Router>
  );
}

export default App;
