import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Assuming axios is used for API requests
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Card, Form, Navbar, Nav, Dropdown, DropdownButton } from 'react-bootstrap';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import CartPage from './cartPage';
import { useCart } from './cartContext';
import ProductDetail from './ProductDetail';
import LoginForm from './LoginForm';
function App() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token'));
  const { addToCart } = useCart();

  useEffect(() => {
    if (!token) return; // Execute the following code only if token exists

    axios.get('https://dummyjson.com/products')
      .then(response => {
        setProducts(response.data.products);
      })
      .catch(error => console.error('Error fetching products:', error));

    axios.get('https://dummyjson.com/products/categories')
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => console.error('Error fetching categories:', error));
  }, [token]); 

  useEffect(() => {
    let url = 'https://dummyjson.com/products';
    if (selectedCategory) {
      url += `/category/${encodeURIComponent(selectedCategory)}`;
    }
    axios.get(url)
      .then(response => {
        const filteredProducts = response.data.products;
        const finalProducts = searchQuery ? filteredProducts.filter(product =>
          product.title.toLowerCase().includes(searchQuery.toLowerCase())) : filteredProducts;
        setProducts(finalProducts);
      })
      .catch(error => console.error('Error fetching products by category:', error));
  }, [selectedCategory, searchQuery]); // Run this effect on category or search query changes

  const handleLoginSuccess = (newToken) => {
    localStorage.setItem('token', newToken);
    setToken(newToken);
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove the token from localStorage
    setToken(null); // Update the state to reflect that the user is logged out
  };

  if (!token) {
    return <LoginForm onLoginSuccess={handleLoginSuccess} />;
  }

  return (
    <Router>
      <Navbar bg="dark" variant="dark" expand="lg" className="mb-4">
        <Container>
          <Navbar.Brand as={Link} to="/">My E-Commerce App</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link as={Link} to="/cart">Cart</Nav.Link>
            </Nav>
            <Form inline className="d-flex">
              <Form.Control
                type="text"
                placeholder="Search by name"
                className="mr-sm-2"
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </Form>
            {token && (
              <MDBBtn outline color="light" onClick={handleLogout} className="ms-2">
                Logout
              </MDBBtn>
            )}
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Container>
        <Row className="justify-content-center mb-3">
          <Col xs={12} md={6}>
            <DropdownButton id="dropdown-basic-button" title={selectedCategory || "Select Category"} className="mb-4">
              <Dropdown.Item onClick={() => setSelectedCategory('')}>All Categories</Dropdown.Item>
              {categories.map((category, index) => (
                <Dropdown.Item key={index} onClick={() => setSelectedCategory(category)}>
                  {category}
                </Dropdown.Item>
              ))}
            </DropdownButton>
          </Col>
        </Row>
        <Row>
          {products.map(product => (
            <Col key={product.id} sm={12} md={6} lg={4} xl={3} className="mb-4">
              <Card className="h-100 shadow-sm">
                <Link to={`/product/${product.id}`}>
                  <Card.Img variant="top" src={product.thumbnail} className="product-image"/>
                </Link>
                <Card.Body className="d-flex flex-column">
                  <Card.Title>{product.title}</Card.Title>
                  <Card.Text>${product.price}</Card.Text>
                  <div className="mt-auto">
                    <MDBBtn onClick={() => addToCart(product)} className="w-100">Add to Cart</MDBBtn>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>

      <Routes>
        <Route path="/cart" element={<CartPage />} />
        <Route path="/product/:productId" element={<ProductDetail />} />
        <Route path="/" element={<React.Fragment />} />
      </Routes>
    </Router>
  );

}

export default App;
